mapper.update:
UPDATE student_course
status = #{status}, regular_score = #{regularScore}, exam_score = #{examScore}, final_score = #{finalScore}, regular_score_date = #{regularScoreDate}, exam_score_date = #{examScoreDate}, final_score_date = #{finalScoreDate}, remark = #{remark},
WHERE student_sid = #{studentSid} AND course_cid = #{courseCid} AND semester = #{semester}

mapper.delete:
DELETE FROM student_course
WHERE student_sid = #{studentSid}
AND course_cid = #{courseCid}
AND semester = #{semester}

mapper.select:
SELECT 
,

FROM student_course sc
LEFT JOIN student s ON sc.student_sid = s.sid
LEFT JOIN course c ON sc.course_cid = c.cid
WHERE sc.student_sid = #{studentSid}
AND sc.course_cid = #{courseCid}
AND sc.semester = #{semester}

SELECT ,
FROM student_course sc LEFT JOIN student s ON sc.student_sid = s.sid LEFT JOIN course c ON sc.course_cid = c.cid WHERE sc.student_sid = #{studentSid}
AND sc.semester = #{semester}
ORDER BY sc.selection_date DESC

SELECT ,
FROM student_course sc LEFT JOIN student s ON sc.student_sid = s.sid LEFT JOIN course c ON sc.course_cid = c.cid WHERE sc.course_cid = #{courseCid}
AND sc.semester = #{semester}
ORDER BY sc.selection_date DESC
